import './App.css';
import {useState} from 'react';

// react에서 사용자 정의 Tag를 만들때는 반드시 대문자로 시작해야 한다.
function Header(props) {
  return <header>
    <h1><a href="/" onClick={(event)=>{
      event.preventDefault(); // 기본동작인 '/'로 이동하는 것을 막는다.
      props.onChangeMode();
    }}>{props.title}</a></h1>
  </header>
}

function Nav(props) {
  const list = [

  ];

  for (let i=0; i<props.topics.length; i++) {
    let t = props.topics[i];
    list.push(<li key={t.id}>
      <a id={t.id} href={'/read'+t.id} onClick={event=>{
        event.preventDefault();
        props.onChangeMode(Number(event.target.id));
      }}>{t.title}</a></li>);
  }

  return <nav>
    <ol>
      {list}
    </ol>
  </nav>
}

function Article(props) {
  return <article>
    <h2>{props.title}</h2>
    {props.body}
  </article>
}

function Create(props) {
  return <article>
      <h2>Create</h2>
      <form onSubmit={event=>{
        // submit 버튼을 클릭해서 form에서 onSubmit event가 발생하면 page가 reload 된다. 그것을 막는다.
        event.preventDefault();
        // event.target => form
        const title = event.target.title.value;
        const body = event.target.body.value;
        props.onCreate(title, body);
      }}>
        <p><input type="text" name="title" placeholder="title"/></p>
        <p><textarea name="body" placeholder="body"></textarea></p>
        <p><input type="submit" value="Create"></input></p>
      </form>
    </article>
}

function Update(props){
  const [title, setTitle] = useState(props.title);
  const [body, setBody] = useState(props.body);
  return <article>
    <h2>Update</h2>
    <form onSubmit={event => {
      // submit 버튼을 클릭해서 form에서 onSubmit event가 발생하면 page가 reload 된다. 그것을 막는다.
      event.preventDefault();
      // event.target => form
      const title = event.target.title.value;
      const body = event.target.body.value;
      props.onUpdate(title, body);
    }}>
      <p><input type="text" name="title" placeholder="title" value={title} onChange={event=>{
        setTitle(event.target.value);
      }}/></p>
      <p><textarea name="body" placeholder="body" value={body} onChange={event=>{
        setBody(event.target.value);
      }}></textarea></p>
      <p><input type="submit" value="Update"></input></p>
    </form>
  </article>
}

function App() {
  const [mode, setMode] = useState('WELCOME');
  const [id, setId] = useState(null);
  const [nextId, setNextId] = useState(4);
  const [topics, setTopics] = useState([ 
    { id: 1, title: 'html', body: 'html is ...' },
    { id: 2, title: 'css', body: 'css is ...' },
    { id: 3, title: 'javascript', body: 'javascript is ...' } 
  ]);

  let content = null;
  let contextControl = null;

  if (mode === 'WELCOME') {
    content = <Article title="Welcome" body="Hello Web!"></Article>
  } else if (mode === 'READ') {
    let title, body = null;

    for(let i = 0; i < topics.length; i++) {
      if (topics[i].id === id) {
        title = topics[i].title;
        body = topics[i].body;
      }
    }
    content = <Article title={title} body={body}></Article>
    contextControl = <>
      <li><a href={'/update/'+id} onClick={event => {
        event.preventDefault();
        setMode('UPDATE');
      }}>Update</a></li>
    </>
  } else if (mode === 'CREATE') {
    content = <Create onCreate={(_title, _body)=>{
      /*
        const newTopic = {id: nextId, title: _title, body: _body};
        topics.push(newTopic);
        setTopics(topics);
        // 이렇게 하면 안된다. setTopics(topics) 에서, topics는 primitive type이 아니라 reference type이기 때문에 
        // 비교를 할 때, 주소값을 비교하게 된다. 그러므로 원본 topics와 setTopics로 들어온 topics는 같은 객체이고, 
        // 같은 주소값을 가지고 있기 때문에 변경이 되지 않았다고 판단해서 App 컴포넌트가 다시 랜더링되지 않는다.
       */
      const newTopics = [...topics];
      const newTopic = {id: nextId, title: _title, body: _body};
      newTopics.push(newTopic);
      setTopics(newTopics);
      setMode('READ');
      setId(nextId);
      setNextId(nextId + 1);
    }}></Create>
  } else if (mode === 'UPDATE') {
    let title, body = null;

    for (let i = 0; i < topics.length; i++) {
      if (topics[i].id === id) {
        title = topics[i].title;
        body = topics[i].body;
      }
    }

    content = <Update title={title} body={body} onUpdate={(title, body)=>{
      const newTopics = [...topics];
      const updatedTopic = {id: id, title: title, body: body};

      for (let i = 0; i < newTopics.length; i++) {
        if (newTopics[i].id === id) {
          newTopics[i] = updatedTopic;
          break;
        }
      }

      setTopics(newTopics);
      setMode('READ');
    }}></Update>
  }

  return (
    <div>
      <Header title="WEB" onChangeMode={()=>{
        setMode('WELCOME');
      }}></Header>
      <Nav topics={topics} onChangeMode={(_id)=>{
        setMode('READ');
        setId(_id);
      }}></Nav>
      {content}
      <ul>
        <li>
          <a href="/create" onClick={event=>{
            event.preventDefault();
            setMode('CREATE');
            }}>Create</a>
        </li>
        {contextControl}
      </ul>
    </div>
  );
}

export default App;
